package controlador;

public class Ejercicio10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// EJERCICIO 10
		// Muestre en un ejemplo como se usan los operadores incremento y decremento.
		int cartas = 9;
		System.out.println(cartas);
		cartas++;
		System.out.println(cartas);
		System.out.println(++cartas);
		System.out.println(++cartas);
		System.out.println(++cartas);
		System.out.println(cartas++);
		System.out.println(cartas++);
		System.out.println(cartas--);
		System.out.println(cartas--);
		System.out.println(--cartas);
		System.out.println(--cartas);
		System.out.println(cartas--);
		System.out.println(--cartas);

	}

}
